data.custom_css:
@dontimport url('https://fonts.googleapis.com/css2?family=Archivo&display=swap');

body {
  background: url(https://www.tourismdata.ch/wp-content/uploads/2023/01/ai-002-1.jpg) 50% 60px;
  font-optical-sizing: auto;
  font-weight: 400;
  font-style: normal;
}
.navbar, .row div .card { background: white; }
.theme-dark .navbar, .theme-dark .row div .card { background: black; }

.container {   background: #eee; }
.theme-dark .container {   background: #111; }
.container.with-event {   margin-top: 0px; padding-top: 2em; }
.container.event-header {  
  padding-bottom: 1em;
  border-top-left-radius: 1em;
  border-top-right-radius: 1em;
}
.section-header-content .event-date {  padding-top: 0.7em; }
.event-footnote { 
  margin-bottom: 1em; padding: 2em;
  background: white; color: black;
}

.jumbotron.event-instruction, .event-home .event-info { box-shadow:none; border: none; }
.event-home .event-info .event-description { font-size: 130%; }
#event-logo {
  max-height: 4em;
  margin-top: 2em;
  margin-bottom: 2em;
}
.section-header-content { text-align: center; }
.section-header .event-name { text-align: left; font-size: 340%; }

h2 > a { text-decoration: none; }

.stages-page .list-stages .step .emoji {
  background: lightgray;
  display: inline-block;
  width: 1.8em;
  height: 1.8em;
  border-radius: 2em;
  padding-top: 9px;
}
.stages-page .list-stages .step .title {
  margin-left: 7pt;
}
.stages-page .step .subtitle {
  width: 86% !important;
}
